<?php//// Базовый контроллер сайта.//abstract class C_Base extends C_Controller{	protected $title;		// заголовок страницы	protected $content;		// содержание страницы	protected $needLogin;	// необходима ли авторизация	protected $front_c;	protected $err;	protected $msg;	//	// Конструктор.	//	function __construct()	{			$this->needLogin = false;		$this->front_c = C_Front::Instance();		$this->err = array();	}		protected function before()	{		$this->title = 'Комментарии';		$this->content = '';	}		//	// Генерация базового шаблонаы	//		public function render()	{		$val = isset($this->params["ajax"]) ? (int)$this->params["ajax"] : 0;		if($val==0) {//Значит не ajax			$vars = array('title' => $this->title, 'content' => $this->content, "controller" => $this->front_c->getControllerName());			$page = $this->Template('v/v_main.php', $vars);			$this->front_c->setBody($page);		}	}}?>