<?php//загрузкаfunction __autoload($classname){	switch($classname[0])	{		case 'C':			include_once("c/$classname.php");			break;		case 'M':			include_once("m/$classname.php");			break;	}}define('BASE_URL', '/');define('DB_HOST', 'localhost');define('DB_USER', 'root');define('DB_PASSWORD', '');define('DB_NAME', 'test-comments');?>